import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
    body {
        padding : 0;
        margin : 0;
        overflow-y : hidden;
        background: #0A192F;
    }

    html {
        width: 100%;
    }

    svg < * {
        position: absolute;
        top: 0;
        right : 0;
    }
`;

export { GlobalStyle };
