import React from "react";
import { animated } from "react-spring";
import styled from "styled-components";
import { sideLink } from "./config";
import { X } from "react-feather";

interface PropTypes {
  animatedSidebar: any;
  toggleSideBar: (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => void;
}

const SideBarWrapper = styled.div`
  background: #172a45;
  list-style: none;
  position: fixed;
  height: 100vh;
  width: 100%;

  div {
    text-align: right;
    padding: 50px 50px;
  }

  li {
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto;
  }

  /* #curveSvg {
    position: absolute;
    bottom: 0;
    left: 0;
  } */
`;

const Sidebar: React.FC<PropTypes> = ({ animatedSidebar, toggleSideBar }) => {
  return (
    <animated.div style={animatedSidebar}>
      <SideBarWrapper>
        <div onClick={(event) => toggleSideBar(event)}>
          <X color="#fff" />
        </div>
        {sideLink.map((link: any) => (
          <li key={link.id}>{link.name}</li>
        ))}
        {/* 
        <div id="curveSvg">
          <svg
            width="503"
            height="487"
            viewBox="0 0 503 487"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              fill-rule="evenodd"
              clip-rule="evenodd"
              d="M-1.11198 490.24L502.353 489.327C486.935 479.874 469.31 472.27 449.326 466.812C295.751 424.871 291.55 325.322 287.483 228.963C282.497 110.81 277.713 -2.54749 -1.99994 0.97246L-1.11198 490.24Z"
              fill="#383D5B"
            />
          </svg>
        </div> */}
      </SideBarWrapper>
    </animated.div>
  );
};

export default Sidebar;
