import React from "react";
import { animated } from "react-spring";
import styled from "styled-components";
import { sideLink } from "../utils/config";
import { X } from "react-feather";
import { Helmet } from "react-helmet";

const Wrapper = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  z-index: 1;

  @media (max-width: 768px) {
    width: min(55vw, 400px);
  }

  @media (max-width: 480px) {
    width: min(60vw, 400px);
  }
`;

const SideBarWrapper = styled.div`
  list-style: none;
  background-color: var(--light-navy);
  height: 100vh;
  border: 0;
  width: min(65vw, 350px);

  div {
    text-align: right;
    padding: 30px 30px;
    cursor: pointer;
  }

  ul {
    padding: 0;
    margin: 0;
    width: 100%;
    text-align: center;

    li {
      list-style-type: circle;
      position: relative;
      margin: 0 auto 60px;
      font-size: clamp(var(--fz-sm), 4vw, var(--fz-lg));
      font-size: 30px;
    }
  }
`;

interface PropTypes {
  animatedSidebar: any;
  toogle: boolean;
  toggleSideBar: (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => void;
}

const Sidebar: React.FC<PropTypes> = ({
  animatedSidebar,
  toggleSideBar,
  toogle,
}) => {
  return (
    <>
      <Helmet>
        <body className={toogle ? "blur" : ""} />
      </Helmet>
      <Wrapper>
        <animated.div style={animatedSidebar}>
          <SideBarWrapper>
            <div onClick={(event) => toggleSideBar(event)}>
              <X color="#fff" />
            </div>
            <ul>
              {sideLink.map((link: any) => (
                <li key={link.id}>{link.name}</li>
              ))}
            </ul>
          </SideBarWrapper>
        </animated.div>
      </Wrapper>
    </>
  );
};

export default Sidebar;
