import React from "react";
import { animated } from "react-spring";
import styled from "styled-components";
import { sideLink } from "./config";
import { X } from "react-feather";

interface PropTypes {
  animatedSidebar: any;
  toggleSideBar: (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => void;
}

const SideBarWrapper = styled.div`
  background: #172a45;
  list-style: none;
  position: fixed;
  height: 100vh;
  width: 100%;

  section {
    text-align: right;
    padding: 35px 35px;
    cursor: pointer;
  }

  li {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-family: var(--font-sans);
    color: var(--lightest-slate);
    line-height: 100px;
    font-size: 50px;
  }

  #curveSvg {
    position: absolute;
    bottom: 0;
    left: 0;
  }
`;

const Sidebar: React.FC<PropTypes> = ({ animatedSidebar, toggleSideBar }) => {
  return (
    <animated.div style={animatedSidebar}>
      <SideBarWrapper>
        <section onClick={(event) => toggleSideBar(event)}>
          <X color="#fff" />
        </section>
        {sideLink.map((link: any) => (
          <li key={link.id}>{link.name}</li>
        ))}

        <div id="curveSvg">
          <svg
            width="504"
            height="492"
            viewBox="0 0 504 492"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              fill-rule="evenodd"
              clip-rule="evenodd"
              d="M-0.115802 1039.13L-1.10771 492.577L502.357 491.663C486.939 482.165 469.314 474.525 449.331 469.041C295.755 426.898 291.553 326.874 287.485 230.055C282.498 111.337 277.713 -2.56159 -1.99997 0.972733L-0.115802 1039.13Z"
              fill="#383D5B"
            />
          </svg>
        </div>
      </SideBarWrapper>
    </animated.div>
  );
};

export default Sidebar;
