import React from "react";
import styled from "styled-components";

const StyledAboutSection = styled.section`
  max-width: 900px;
  .inner {
    display: grid;
    grid-template-columns: 3fr 2fr;
    grid-gap: 50px;

    @media (max-width: 768px) {
      display: block;
    }
  }
`;

const About = () => {
  return (
    <div className="container">
      <div className="container-wrapper">
        <StyledAboutSection>
          <h2 className="numbered-heading">About Me</h2>

          <StyledText>
            <div>
              <p>
                Hello! I'm Brittany, a software engineer based in Boston, MA.
              </p>

              <p>
                I enjoy creating things that live on the internet, whether that
                be websites, applications, or anything in between. My goal is to
                always build products that provide pixel-perfect, performant
                experiences.
              </p>

              <p>
                Shortly after graduating from{" "}
                <a href="https://www.ccis.northeastern.edu">
                  Northeastern University
                </a>
                , I joined the engineering team at{" "}
                <a href="https://www.upstatement.com">Upstatement</a> where I
                work on a wide variety of interesting and meaningful projects on
                a daily basis.
              </p>

              <p>
                Here are a few technologies I've been working with recently:
              </p>
            </div>

            <ul className="skills-list">
              {skills && skills.map((skill, i) => <li key={i}>{skill}</li>)}
            </ul>
          </StyledText>
        </StyledAboutSection>
      </div>
    </div>
  );
};

export default About;
