import React from "react";
import { animated } from "react-spring";
import styled from "styled-components";
import { sideLink } from "./config";
import { X } from "react-feather";

interface PropTypes {
  animatedSidebar: any;
  toggleSideBar: (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => void;
}

const SideBarWrapper = styled.div`
  background: var(--light-navy);
  list-style: none;
  position: fixed;
  height: 100vh;
  width: 25%;
  border: 0;
  color: inherit;
  color: inherit;
  text-transform: none;
  transition-timing-function: linear;
  transition-duration: 0.15s;
  transition-property: opacity, filter;

  @media (max-width: 768px) {
    width: 60%;
  }

  @media (max-width: 480px) {
    width: 70%;
  }

  section {
    text-align: right;
    padding: 30px 30px;
    cursor: pointer;
  }

  li {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-family: var(--font-sans);
    color: var(--lightest-slate);
    line-height: 100px;
    font-size: 30px;
  }
`;

const Sidebar: React.FC<PropTypes> = ({ animatedSidebar, toggleSideBar }) => {
  return (
    <SideBarWrapper>
      <section onClick={(event) => toggleSideBar(event)}>
        <X color="#fff" />
      </section>
      {sideLink.map((link: any) => (
        <li key={link.id}>{link.name}</li>
      ))}
    </SideBarWrapper>
  );
};

export default Sidebar;
